import React, { useState, useEffect } from 'react';
import './App.css';
import Form from './components/Form';
import TodoList from './components/TodoList';



const App = () => {
  const [inputValue, setInputValue] = useState('')
  const [listData, setListData] = useState([])
  const [status, setStatus] = useState('all')
  const [filteredState, setFilteredState] = useState([])


  useEffect(() => {
    filterHandler()
  }, [status, listData]
  )

  useEffect(() => {
    console.log(listData)
    console.log(filterState)
  }, [listData]
  )
  const filterHandler = () => {
    debugger
    switch (status) {
      case 'completed':
        setFilteredState(listData.filter(el => el.status === true))
        break
      case 'uncompleted':
        setFilterState(listData.filter(el => el.status === false))
        break
      case 'all':
        setFilterState(listData)
        break
    }
  }
  return (
    <div className="App">
      <header>
        <h1>ToDo LiSt</h1>
      </header>
      <Form inputValue={inputValue} setInputValue={setInputValue}
        listData={listData} setListData={setListData}
        setStatus={setStatus}/>
      <TodoList listData={listData} setListData={setListData} filterState={filterState}/>
    </div>
  );
}

export default App;
