import React from 'react'

const Form = ({ inputValue, setInputValue, listData, setListData }) => {
    const buttonHandler = (e) =>{
        e.preventDefault();
        setListData([...listData, { value: inputValue, status: false, key: Math.random() }]);
        setInputValue('')
        console.log(1)
    }
    return (
        <form>
            <div className="choose">
                <select>
                    <option value="all">all</option>
                    <option valut="completed">completed</option>
                    <option valut="uncompleted">uncompleted</option>
                </select>
            </div>
            <input onChange={(e) => setInputValue(e.target.value)} value={inputValue}></input>
            <button onClick={(e) => buttonHandler(e)}>add</button>
        </form >
    )
}

export default Form;
